"use strict";class SmartMessage{static get unexpected_error(){return"216unexpected_error"}static serialize(e){const t=e.length,s=t.toString().length;return 1!=s.toString().length&&console.error(this.unexpected_error+"Size of message length must be 1 byte"),s.toString()+t.toString()+e}static messageLengthSize(e){const t=parseInt(e.substr(0,1));return t<=0&&!isNaN(t)&&console.error(this.unexpected_error+"Size of message length must be a number and cannot be less or equal to 0"),t}static messageLength(e,t=-1){-1==t&&(t=this.messageLengthSize(e));const s=parseInt(e.substr(1,t));return s}static messageTotalLength(e){const t=this.messageLengthSize(e),s=this.messageLength(e,t);return 1+t+s}static removeMessage(e){return e.substr(this.messageTotalLength(e))}static getMessage(e){const t=this.messageLengthSize(e),s=this.messageLength(e,t);return e.substr(1+t,s)}}